import {React, useState} from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

/*
const daysOfWeekSuggestions = [
    {label: "Monday"},
    {label: "Tuesday"},
    {label: "Wednesday"},
    {label: "Thursday"},
    {label: "Friday"},
    {label: "Saturday"},
    {label: "Sunday"}
];
*/

const initDaysOptions = [
    {label: "Every day", disabled: false},
    {label: "Sunday", disabled: false},
    {label: "Monday", disabled: false},
    {label: "Tuesday", disabled: false},
    {label: "Wednesday", disabled: false},
    {label: "Thursday", disabled: false},
    {label: "Friday", disabled: false},
    {label: "Saturday", disabled: false}
];

const medicationSuggestions = [
    {label: "Lisinopril"},
    {label: "Valsartan"}
];

const validationSchema = Yup.object().shape({
    medication: Yup.string()
        .required("Medication is required"),
    daysOfWeek: Yup.array()
        .of(
            Yup.object().shape({
                value: Yup.string(),
                label: Yup.string()
            })
        )
        .min(1, "Days of the week are required")
});

export default function RegisterReminder() {
    //Styles and SX
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const {handleSubmit, control} = useForm({
        resolver: yupResolver(validationSchema)
    });

    const onSubmit = (data) => {
        console.log(data);
    };

    return (
        <Container>
            <form onSubmit={handleSubmit(onSubmit)} noValidate>
                <Box
                    sx={boxSx}
                >
                    <Controller
                        name="medication"
                        control={control}
                        defaultValue={""}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                sx={{
                                    display: 'inline-block',
                                    '& input': {
                                        width: 200,
                                        bgcolor: 'background.paper',
                                        color: (theme) =>
                                            theme.palette.getContrastText(theme.palette.background.paper),
                                    },
                                }}
                                disableClearable
                                isOptionEqualToValue={(option, value) => option.id === value.id}
                                id="medication-autocomplete"
                                onChange={(event, value) => field.onChange(value.label)}
                                options={medicationSuggestions}
                                renderInput={(params) => (
                                    <TextField
                                        required
                                        error={!!error}
                                        helperText={error?.message}
                                        id="medication"
                                        label="Medication"
                                        name="medication"
                                        type="search"
                                        inputRef={ref}
                                        {...params}
                                    />
                                )}
                            />
                        )}
                    />

                    <Controller
                        name="daysOfWeek"
                        control={control}
                        defaultValue={[]}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                disableClearable
                                disablePortal
                                filterSelectedOptions
                                multiple
                                getOptionDisabled={(option) => option.disabled ? true : false}
                                getOptionLabel={(option) => option.label}
                                id="days-autocomplete"
                                onChange={(event, value) => field.onChange(value)}
                                options={daysOfWeekSuggestions}
                                renderInput={(params) => (
                                    <TextField
                                        required
                                        error={!!error}
                                        helperText={error?.message}
                                        id="daysOfWeek"
                                        label="Days of the week"
                                        name="daysOfWeek"
                                        type="search"
                                        inputRef={ref}
                                        {...params}
                                    />
                                )}
                            />
                        )}
                    />

                    <Button size="large" sx={buttonSx} type="submit" variant="contained">Submit</Button>
                </Box>
            </form>
        </Container>
    );
}
