import {useState, useCallback} from "react";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from '@mui/material/CardContent';
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";

export default function DisplayMedications() {

    function deleteMedication(medicationId, parsedMedications) {
        console.log("clicked");
        let newParsed = parsedMedications.splice(medicationId, 1);
        console.log(parsedMedications);
        // console.log(newParsed);
        localStorage.setItem("medications", JSON.stringify(newParsed));

    }

    const buttonSx = {
        borderRadius: 0,
        fontSize: "17px",
        height: "5.75ch",
        width: "50%"
    };

    const cardSx = {
        bgcolor: "#eaeef3",
        boxShadow: 3,
        m: 5
    };

    const typographyMedicationSx = {
        textAlign: "left"
    };

    let medications;

    if (localStorage.getItem("medications") !== null) {
        let medicationList = localStorage.getItem("medications");
        let parsed = JSON.parse(medicationList);

        medications = parsed.map((medication) => {
            return (
                <Card key={medication.id} sx={cardSx}>
                    <CardContent>
                        <Typography key={medication.medication.id} sx={typographyMedicationSx} variant="h5">
                            Medication: {medication.medication}
                        </Typography>
                        <Typography key={medication.dose.id} sx={typographyMedicationSx} variant="h5">
                            Dose: {medication.dose} mg
                        </Typography>
                    </CardContent>
                    <Box>
                        <Button size="large" sx={buttonSx} variant="contained">Edit Med</Button>
                        <Button color="error" size="large"
                                sx={buttonSx} variant="contained">Delete
                            Med</Button>
                    </Box>
                </Card>
            )
        });
    } else {
        medications = () => {
            return (
                <Card sx={cardSx} variant="outlined">
                    <CardContent>
                        <Typography sx={typographyMedicationSx} variant="h5">
                            No medications are registered.
                        </Typography>
                    </CardContent>
                    <Box>
                        <Button href="/add-medication" size="large" sx={buttonSx} variant="contained">Register
                            med</Button>
                    </Box>
                </Card>
            )
        };
    }

    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                {medications}
            </Box>
        </Container>
    )
}
