import {useState, forwardRef} from "react";
import {Controller, useForm} from "react-hook-form";
import * as Yup from "yup";
import {yupResolver} from "@hookform/resolvers/yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CloseIcon from '@mui/icons-material/Close';
import Container from "@mui/material/Container";
import IconButton from '@mui/material/IconButton';
import Typography from "@mui/material/Typography";

import Autocomplete from "@mui/material/Autocomplete";
import Dialog from "@mui/material/Dialog";
import DialogTitle from '@mui/material/DialogTitle';
import Slide from "@mui/material/Slide";
import TextField from "@mui/material/TextField";

const doseSuggestions = [
    {label: "10"},
    {label: "20"},
    {label: "30"},
    {label: "40"},
    {label: "50"},
    {label: "60"},
    {label: "70"},
    {label: "80"}
];

const Transition = forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

// Form validation using Yup
const validationSchema = Yup.object().shape({
    dose: Yup.string()
        .required("Dose is required")
});

export default function DisplayMedications() {
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        borderRadius: 0,
        fontSize: "17px",
        height: "5.75ch",
        width: "50%"
    };

    const cardSx = {
        bgcolor: "#eaeef3",
        boxShadow: 3,
        m: 5
    };

    const dialogTitleSx = {
        m: 0,
        p: 3
    };

    const formButtonSx = {
        borderRadius: 0,
        fontSize: "17px",
        height: "5.75ch",
        mb: 5,
        width: "100%"
    };

    const iconButtonSx = {
        position: 'absolute',
        right: 8,
        top: 8,
        color: (theme) => theme.palette.grey[500],
    };

    const typographyMedicationSx = {
        textAlign: "left"
    };

    const updateDoseTypography = {
        mb: 5,
        ml: 5,
        mr: 5
    };

    const Medication = (medication, index, parsed) => {
        const {handleSubmit, control, formState} = useForm({
            mode: "onChange",
            resolver: yupResolver(validationSchema)
        });

        const [, setSelectedMedication] = useState(null)

        // This handles the delete medication confirmation dialog
        const [open, setOpen] = useState(false);
        const handleClickOpen = (index) => {
            setOpen(true);
            setSelectedMedication(index);
        };
        const handleClose = () => {
            setOpen(false);
            setSelectedMedication(null);
        };

        // This handles the update medication dialog
        const [openUpdate, setOpenUpdate] = useState(false);
        const handleClickOpenUpdate = (index) => {
            setOpenUpdate(true);
            setSelectedMedication(index);
        };
        const handleCloseUpdate = () => {
            setOpenUpdate(false);
            setSelectedMedication(null);
        };

        // Function for the delete med button
        function deleteMedication(medicationId, parsedMedications) {
            if (parsedMedications.length > 1) {
                parsedMedications.splice(medicationId, 1);
            } else {
                parsedMedications = [];
            }

            localStorage.setItem("medications", JSON.stringify(parsedMedications));

            // This forces the dialog to close
            setOpen(false);
            setSelectedMedication(null);
        }

        // Function for the update dose button
        function updateDose(medicationId, parsedMedications, data) {
            parsedMedications[medicationId].dose = data.dose;
            localStorage.setItem("medications", JSON.stringify(parsedMedications));

            // This forces the dialog to close
            setOpenUpdate(false);
            setSelectedMedication(null);
        }

        return (
            <Box key={"box1" + index}>
                <Card key={"card" + index} sx={cardSx}>
                    <CardContent key={"cardContent" + index}>
                        <Typography key={"typoMedication" + index} sx={typographyMedicationSx} variant="h5">
                            Medication: {medication.medication}
                        </Typography>
                        <Typography key={"typoDose" + index} sx={typographyMedicationSx} variant="h5">
                            Dose: {medication.dose} mg
                        </Typography>
                    </CardContent>
                    <Box key={"box2" + index}>
                        <Button key={"editButton" + index} onClick={() => handleClickOpenUpdate(index)} size="large"
                                sx={buttonSx}
                                variant="contained">Edit
                            Dose</Button>
                        <Button key={"deleteButton" + index} onClick={() => handleClickOpen(index)} color="error"
                                size="large"
                                sx={buttonSx} variant="contained">Delete
                            Med </Button>
                    </Box>
                </Card>

                {/* Delete medication dialog */}
                <Dialog
                    key={"deleteDialog" + index}
                    open={open}
                    onClose={handleClose}
                    TransitionComponent={Transition}
                >
                    <Typography key={"typoSure" + index} variant="h5" sx={{m: 5}}> Are you sure you want to delete
                        the
                        medication? </Typography>

                    <Box key={"box3" + index}>
                        <Button key={"yesButton" + index} onClick={() => deleteMedication(medication.id, parsed)}
                                size="large"
                                sx={buttonSx} variant="contained">Yes</Button>
                        <Button key={"noButton" + index} onClick={handleClose} color="error"
                                size="large"
                                sx={buttonSx} variant="contained">No</Button>
                    </Box>
                </Dialog>

                {/* Update medication dialog */}
                <Dialog
                    key={"updateDialog" + index}
                    open={openUpdate}
                    onClose={handleCloseUpdate}
                    TransitionComponent={Transition}
                >
                    <DialogTitle key={"dialogTitle" + index} sx={dialogTitleSx}>
                        {handleCloseUpdate ? (
                            <IconButton
                                key={"iconButton" + index}
                                aria-label="close"
                                onClick={handleCloseUpdate}
                                sx={iconButtonSx}
                            >
                                <CloseIcon key={"closeIcon" + index}/>
                            </IconButton>
                        ) : null}
                    </DialogTitle>

                    <form
                        key={"form" + index}
                        onSubmit={handleSubmit((data) => updateDose(medication.id, parsed, data))}
                        noValidate>
                        <Typography key={"typoUpdate" + index} sx={updateDoseTypography} variant="h4">
                            Update dose
                        </Typography>

                        <Box
                            key={"box4" + index}
                            sx={boxSx}
                        >
                            <Controller
                                key={"controller" + index}
                                name="dose"
                                control={control}
                                defaultValue={""}
                                render={({field: {ref, ...field}, fieldState: {error}}) => (
                                    <Autocomplete
                                        key={"autocomplete" + index}
                                        {...field}
                                        autoHighlight
                                        disableClearable
                                        isOptionEqualToValue={(option, value) => option.id === value.id}
                                        id="dose-autocomplete"
                                        onChange={(event, value) => field.onChange(value.label)}
                                        options={doseSuggestions}
                                        renderInput={(params) => (
                                            <TextField
                                                key={"textfield" + index}
                                                required
                                                error={!!error}
                                                helperText={error?.message}
                                                id="dose"
                                                label="Dose"
                                                name="dose"
                                                type="numeric"
                                                inputRef={ref}
                                                {...params}
                                            />
                                        )}
                                    />
                                )}
                            />

                            <Button key={"submitButton" + index} disabled={!formState.isValid} size="large"
                                    sx={formButtonSx} type="submit"
                                    variant="contained">Submit</Button>
                        </Box>
                    </form>
                </Dialog>
            </Box>
        )
    };

    let medications;

    // I also set this condition (localStorage.getItem("medications").length < 2) because if the array is empty, the length === 2
    if (localStorage.getItem("medications") !== null && localStorage.getItem("medications").length > 2) {
        let medicationList = localStorage.getItem("medications");
        let parsed = JSON.parse(medicationList);

        medications = parsed.map((medication) => {
            return (<Medication medication={medication}/>)
        });
    } else {
        medications =
            (<Card sx={cardSx}>
                <CardContent>
                    <Typography sx={typographyMedicationSx} variant="h5">
                        No medications are registered.
                    </Typography>
                </CardContent>
                <Box>
                    <Button href="/add-medication" size="large" sx={Object.assign(buttonSx, {width: "100%"})}
                            variant="contained">Register
                        med</Button>
                </Box>
            </Card>)
        ;
    }

    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                {medications}
            </Box>
        </Container>
    )
}
