import * as React from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

const doseSuggestions = [
    {label: "10", id: 1},
    {label: "20", id: 2},
    {label: "30", id: 3},
    {label: "40", id: 4},
    {label: "50", id: 5},
    {label: "60", id: 6},
    {label: "70", id: 7},
    {label: "80", id: 8}
];

const medicationSuggestions = [
    {label: "Lisinopril", id: 1},
    {label: "Valsartan", id: 2}
];

// Form validation using Yup
const validationSchema = Yup.object().shape({
    medication: Yup.string()
        .required("Medication is required"),
    dose: Yup.string()
        .required("Dose is required")
});

export default function AddMedication() {
    // Styles
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const typographyMedicationSx = {
        mb: 4,
        textAlign: "center"
    };

    const onSubmit = (data) => {
        // This saves the medications into a local session storage for the purposes of displaying the medications on another page.
        // If I were going to create a deployed version of the app, I would create a DB and save the data there.
        // I also set this condition --> localStorage.getItem("medications").length < 2 --> because if the array is empty, the length === 2
        if (localStorage.getItem("medications") === null || localStorage.getItem("medications").length < 3) {
            localStorage.setItem("medications", JSON.stringify([{
                id: 0,
                medication: data.medication,
                dose: data.dose
            }]));
        } else {
            let medicationList = localStorage.getItem("medications");
            let parsed = JSON.parse(medicationList);
            let newId = parsed[parsed.length - 1].id + 1;
            parsed.push({id: newId, medication: data.medication, dose: data.dose});
            localStorage.setItem("medications", JSON.stringify(parsed));
        }

        fetch("http://localhost:3000/confirmation?medication=" + data.medication + "&dose=" + data.dose)
            .then((resp) => {
                window.location = "http://localhost:3000/confirmation?medication=" + data.medication + "&dose=" + data.dose;
            });
    };

    const {handleSubmit, control, formState} = useForm({
        mode: "onChange",
        resolver: yupResolver(validationSchema)
    });

    return (
        <Container>
            <form onSubmit={handleSubmit(onSubmit)} noValidate>
                <Typography sx={typographyMedicationSx} variant="h4">
                    Add a new medication
                </Typography>

                <Box
                    sx={boxSx}
                >

                    <Controller
                        name="medication"
                        control={control}
                        defaultValue={""}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                autoHighlight
                                disableClearable
                                disablePortal
                                isOptionEqualToValue={true}
                                id="medication-autocomplete"
                                onChange={(event, value) => field.onChange(value.label)}
                                options={medicationSuggestions}
                                renderInput={(params) => (
                                    <TextField
                                        required
                                        error={!!error}
                                        helperText={error?.message}
                                        id="medication"
                                        label="Medication"
                                        name="medication"
                                        type="search"
                                        inputRef={ref}
                                        {...params}
                                    />
                                )}
                            />
                        )}
                    />

                    <Controller
                        name="dose"
                        control={control}
                        defaultValue={""}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                autoHighlight
                                disableClearable
                                disablePortal
                                isOptionEqualToValue={(option, value) => option.id === value.id}
                                id="dose-autocomplete"
                                onChange={(event, value) => field.onChange(value.label)}
                                options={doseSuggestions}
                                renderInput={(params) => (
                                    <TextField
                                        required
                                        error={!!error}
                                        helperText={error?.message}
                                        id="dose"
                                        label="Dose"
                                        name="dose"
                                        type="numeric"
                                        inputRef={ref}
                                        {...params}
                                    />
                                )}
                            />
                        )}
                    />

                    <Button disabled={!formState.isValid} size="large" sx={buttonSx} type="submit"
                            variant="contained">Submit</Button>
                </Box>
            </form>
        </Container>
    );
}
