import {useEffect, useState} from "react";
import {Controller, useForm} from "react-hook-form";

// Material UI Components
import TimePicker from "@mui/lab/TimePicker";
import TextField from "@mui/material/TextField";

export default function TimeInput(params) {
    const {control, setValue, register, getValues} = useForm();
    const name = "Time " + params.counter;
    const [time, setTime] = useState(new Date("2000-01-01T00:00:00"));
    const value = getValues(name);

    useEffect(() => {
        register(name);
    });
    useEffect(() => {
        setTime(value || null);
    }, [setTime, value]);

    return (
        <Controller
            control={control}
            name={name}
            defaultValue={time}
            render={({field: {ref, ...field}}) => (
                <TimePicker
                    {...field}
                    label={name}
                    value={time}
                    onChange={(time) => {
                        setValue(name, time, {shouldValidate: true, shouldDirty: true});
                        setTime(time);
                    }}
                    renderInput={(params) =>
                        <TextField
                            id={name}
                            name={name}
                            inputRef={ref}
                            {...params}
                        />
                    }
                />
            )}
        />
    );
}
