import {useState} from "react";
import {Controller} from "react-hook-form";

// Material UI Components
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";

const timesSuggestions = [
    {label: "1"},
    {label: "2"},
    {label: "3"},
    {label: "4"},
    {label: "5"}
];

// Styles
const autoCompleteSx = {
    width: "100%"
};

export default function NumTimesInput({control, variant}) {
    const [timesValue, setTimesValue] = useState("");

    return (
        <Controller
            name="times"
            control={control}
            render={({field: {ref, ...field}, fieldState: {error}}) => (
                <Autocomplete
                    {...field}
                    autoHighlight
                    disableClearable
                    isOptionEqualToValue={(option, value) => value.label === option.label}
                    id="times-autocomplete"
                    onChange={(_event, value) => {
                        field.onChange(value.label);
                        setTimesValue(value);
                    }}
                    options={timesSuggestions}
                    value={timesValue}
                    sx={autoCompleteSx}
                    renderInput={(tfParams) => (
                        <TextField
                            required
                            error={!!error}
                            helperText={error?.message}
                            id="times"
                            label="Number of Times"
                            name="times"
                            type="search"
                            variant={variant}
                            inputRef={ref}
                            {...tfParams}
                        />
                    )}
                />
            )}
        />
    );
}
