import * as React from "react";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";

export default function Confirmation() {
    const query = new URLSearchParams(window.location.search);

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const typographyMedicationSx = {
        mb: 4,
        textAlign: "center"
    }

    return (
        <Container>
            <Box id="confirmation" style={{display: "none", flexDirection: "column"}}>
                <Typography sx={typographyMedicationSx} variant="h4">
                    You have added {query.get("dose")} mg of {query.get("medication")} to
                    your medication list.
                </Typography>

                <Button size="large" sx={Object.assign(buttonSx, {mb: 5})} type="submit" variant="contained">Add another
                    medication</Button>
                <Button size="large" sx={buttonSx} type="submit" variant="contained">View medication list</Button>
            </Box>
        </Container>
    )
}
