import {useState} from "react";

// Material UI Components
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
import Box from "@mui/material/Box";
import Breadcrumbs from "@mui/material/Breadcrumbs";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Link from "@mui/material/Link";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import Stack from "@mui/material/Stack";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";

// Components for the time inputs
import {TimePicker, MuiPickersUtilsProvider} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";

// The TimePicker component will cause the following warning, but it is a problem on MUI's side that can be ignored.
// https://stackoverflow.com/questions/69675760/mui-react-does-not-recognize-the-renderinput-prop-on-a-dom-element-in-datepi
// Warning: React does not recognize the `renderInput` prop on a DOM element.

function getIndexes() {
    let reminderList = localStorage.getItem("reminders");
    let parsed = JSON.parse(reminderList);
    const mostRecent = parsed[parsed.length - 1];
    return (mostRecent.timesPerDay);
}

export default function AddReminder4() {
    // Styles
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        alignItems: "center",
        bgcolor: "background.paper",
        display: "flex",
        flexDirection: "column",
        ml: "50px",
        mr: "50px",
    };

    const iconButtonSx = {
        fontSize: 75
    };

    const linkSx = {
        color: "#1976d2"
    };

    const stackSx = {
        mb: 5
    };

    const breadcrumbs = [
        <Link key="1" underline="hover" color="inherit" href="/add-reminder" sx={linkSx}>
            Name & Dose
        </Link>,
        <Link key="2" underline="hover" color="inherit" href="/add-reminder/days" sx={linkSx}>
            Days of Week
        </Link>,
        <Link key="3" underline="hover" color="inherit" href="/add-reminder/days/nums" sx={linkSx}>
            # of Times
        </Link>,
        <Typography key="4" color="text.primary">
            Times
        </Typography>
    ];

    const TimeInputs = (params) => {
        let name = "Time " + params.counter;

        const [value, setValue] = useState(new Date("2000-10-10T00:00:00"));

        console.log(value)

        return (
            <TimePicker
                label={name}
                value={value}
                onChange={(value) => {
                    setValue(value);
                }}
                renderInput={(params) =>
                    <TextField
                        id={name}
                        name={name}
                        {...params}
                    />
                }
            />
        );
    };

    const allTimes = [];

    for (let i = 0; i < getIndexes(); i++) {
        allTimes.push(<TimeInputs counter={i + 1} key={"TimeInputs" + i}/>);
    }

    const onSubmit = (data) => {
        console.log(data);
    };

    return (
        <Container>
            <Stack spacing={2} sx={stackSx}>
                <Breadcrumbs
                    separator={<NavigateNextIcon fontSize="small"/>}
                    aria-label="breadcrumb"
                >
                    {breadcrumbs}
                </Breadcrumbs>
            </Stack>

            <form onSubmit={onSubmit} noValidate>
                <Box sx={boxSx}>
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        {allTimes}
                    </MuiPickersUtilsProvider>

                    <Box>
                        <Button href="/add-reminder/days/nums"><ArrowBackIcon sx={iconButtonSx}/></Button>

                        <Button disabled={!formState.isValid}
                                type="submit"><ArrowForwardIcon sx={iconButtonSx}/></Button>
                    </Box>
                </Box>
            </form>
        </Container>
    );
}
