import * as React from "react";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from '@mui/material/CardContent';
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";

export default function DisplayMedications() {
    let medications;

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        m: 2.5,
        width: "100%"
    };

    const typographyMedicationSx = {
        mb: 4,
        textAlign: "center"
    }

    if (localStorage.getItem("medications") !== null) {
        let medicationList = localStorage.getItem("medications");
        let parsed = JSON.parse(medicationList);

        medications = parsed.map((medication) => {
            return (
                <Card sx={{m: 5}}>
                    <CardContent>
                        <Typography sx={typographyMedicationSx} variant="h4">
                            Medication: {medication.medication}
                        </Typography>
                        <Typography sx={typographyMedicationSx} variant="h4">
                            Dose: {medication.dose}
                        </Typography>
                    </CardContent>
                    <Button size="large" sx={buttonSx} type="submit" variant="contained">Edit medication</Button>
                    <Button size="large" sx={buttonSx} type="submit" variant="contained">Delete medication</Button>
                </Card>
            )
        });
    }

    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                {medications}
            </Box>
        </Container>
    )
}
