import React, {useState} from "react";
import {Controller, useForm} from 'react-hook-form';

import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";

export default function MedicationInput() {
    const {handleSubmit, reset, control} = useForm();
    const onSubmit = (data) => console.log(data);

    return (
        <form>
            <Controller
                name={"textValue"}
                control={control}
                render={({field: {onChange, value}}) => (
                    <TextField onChange={onChange} value={value} label={"Text Value"}/>
                )}
            />
            <Box
                sx={{
                    "& .MuiTextField-root": {mb: 5, width: "100%"},
                }}
                noValidate
                autoComplete="on"
            >
                <div>
                    <TextField
                        required
                        id="medicationInput"
                        name="medicationInput"
                        label="Medication"
                        type="search"
                        helperText="Input your medication's name"
                    />
                    <br/>
                    <TextField
                        required
                        id="doseInput"
                        name="doseInput"
                        label="Dose"
                        type="number"
                        helperText="Input your medication's dosage in mg"
                    />
                </div>

                <Button variant="contained" size="large" type="submit"
                        sx={{fontSize: "17px", height: "5.75ch", width: "100%"}}
                        onClick={handleSubmit(onSubmit)}>Submit</Button>
            </Box>
        </form>
    );
}
