import * as React from "react";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";

export default function MedicationConfirmation() {
    // Styles
    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const typographyMedicationSx = {
        mb: 4,
        textAlign: "center"
    }

    const query = new URLSearchParams(window.location.search);

    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                <Typography sx={typographyMedicationSx} variant="h4">
                    You have added {query.get("dose")} mg of {query.get("medication")} to
                    your medication list.
                </Typography>

                <Button href="/add-medication" size="large" sx={Object.assign(buttonSx, {mb: 5})}
                        variant="contained">Add another
                    medication</Button>
                <Button href="/medications" size="large" sx={buttonSx} variant="contained">View medication list</Button>
            </Box>
        </Container>
    );
}
