import * as React from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import Box from "@mui/material/Box";
import Breadcrumbs from '@mui/material/Breadcrumbs';
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import NavigateNextIcon from '@mui/icons-material/NavigateNext';
import Stack from '@mui/material/Stack';
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

// Custom Components
import NoRegisteredMedications from "./NoRegisteredMedications";

const validationSchema = Yup.object().shape({
    medication: Yup.string()
        .required("Medication is required"),
    dose: Yup.string()
        .required("Dose is required"),
});

function setSuggestions() {
    // This is the array of medication objects that will be displayed on the cards
    let medicationList = localStorage.getItem("medications");
    let parsed = JSON.parse(medicationList);

    // This will be an array of medication suggestions for the inputs
    let medSuggestions = [];
    let doseSuggestions = [];

    if (parsed !== null && parsed.length > 0) {
        const meds = {};
        const doses = {};

        parsed.forEach((medication, index) => {
            meds[medication.medication] = index;
            doses[medication.dose] = index;
        });

        medSuggestions = Object.keys(meds).map((med) => {
            return {label: med};
        })

        doseSuggestions = Object.keys(doses).map((dose) => {
            return {label: dose};
        })

        return [medSuggestions, doseSuggestions];
    }

    return [];
}

export default function AddReminder1() {
    // Styles
    const autoCompleteSx = {
        width: "100%"
    };

    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        alignItems: "center",
        bgcolor: "background.paper",
        display: "flex",
        flexDirection: "column",
        ml: "50px",
        mr: "50px",
    };

    const typographySx = {
        mb: 5,
        textAlign: "center"
    };

    const {handleSubmit, control, formState} = useForm({
        mode: "onChange",
        resolver: yupResolver(validationSchema)
    });

    const onSubmit = (data) => {
        fetch("http://localhost:3000/add-reminder/?medication=" + data.medication + "&dose=" + data.dose)
            .then((resp) => {
                window.location = "http://localhost:3000/add-reminder/?medication=" + data.medication + "&dose=" + data.dose;
            });
    };

    const breadcrumbs = [
        <Typography key="1" color="text.primary">
            Name & Dose
        </Typography>
    ];

    if (setSuggestions().length > 1) {
        return (
            <Container>
                <Stack spacing={2} sx={{mb: 10}}>
                    <Breadcrumbs
                        separator={<NavigateNextIcon fontSize="small"/>}
                        aria-label="breadcrumb"
                    >
                        {breadcrumbs}
                    </Breadcrumbs>
                </Stack>

                <Typography sx={typographySx} variant="h4">
                    What medication do you want to make a reminder for?
                </Typography>

                <form onSubmit={handleSubmit(onSubmit)} noValidate>
                    <Box
                        sx={boxSx}
                    >
                        <Controller
                            name="medication"
                            control={control}
                            defaultValue={""}
                            render={({field: {ref, ...field}, fieldState: {error}}) => (
                                <Autocomplete
                                    {...field}
                                    autoHighlight
                                    disableClearable
                                    isOptionEqualToValue={(option, value) => option.id === value.id}
                                    id="medication-autocomplete"
                                    onChange={(event, value) => field.onChange(value.label)}
                                    options={setSuggestions()[0]}
                                    sx={autoCompleteSx}
                                    renderInput={(params) => (
                                        <TextField
                                            required
                                            error={!!error}
                                            helperText={error?.message}
                                            id="medication"
                                            label="Medication"
                                            name="medication"
                                            type="search"
                                            inputRef={ref}
                                            {...params}
                                        />
                                    )}
                                />
                            )}
                        />

                        <Controller
                            name="dose"
                            control={control}
                            defaultValue={""}
                            render={({field: {ref, ...field}, fieldState: {error}}) => (
                                <Autocomplete
                                    {...field}
                                    autoHighlight
                                    disableClearable
                                    isOptionEqualToValue={(option, value) => option.id === value.id}
                                    id="dose-autocomplete"
                                    onChange={(event, value) => field.onChange(value.label)}
                                    options={setSuggestions()[1]}
                                    sx={autoCompleteSx}
                                    renderInput={(params) => (
                                        <TextField
                                            required
                                            error={!!error}
                                            helperText={error?.message}
                                            id="dose"
                                            label="Dose"
                                            name="dose"
                                            type="numeric"
                                            inputRef={ref}
                                            {...params}
                                        />
                                    )}
                                />
                            )}
                        />

                        <Button disabled={!formState.isValid}
                                type="submit"><ArrowForwardIcon sx={{fontSize: 75}}/></Button>
                    </Box>
                </form>
            </Container>
        );
    } else {
        return (
            <NoRegisteredMedications/>
        );
    }
}
