import {useState} from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
import Box from "@mui/material/Box";
import Breadcrumbs from "@mui/material/Breadcrumbs";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Link from "@mui/material/Link";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import Stack from "@mui/material/Stack";
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

const initOptions = [
    {label: "Every day", disabled: false},
    {label: "Sunday", disabled: false},
    {label: "Monday", disabled: false},
    {label: "Tuesday", disabled: false},
    {label: "Wednesday", disabled: false},
    {label: "Thursday", disabled: false},
    {label: "Friday", disabled: false},
    {label: "Saturday", disabled: false}
];

// An outline of this solution was provided to me here: https://stackoverflow.com/questions/69878586/how-can-i-validate-an-autocomplete-multiple-textfield-using-react-hook-form-and
const validationSchema = Yup.object().shape({
    daysOfWeek: Yup.array()
        .of(
            Yup.object().shape({
                label: Yup.string(),
                disabled: Yup.boolean()
            })
        )
        .min(1, "Days of the week are required")
});

export default function AddReminder2() {
    // Styles
    const autoCompleteSx = {
        width: "100%"
    };

    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        alignItems: "center",
        bgcolor: "background.paper",
        display: "flex",
        flexDirection: "column",
        ml: "50px",
        mr: "50px",
    };

    const iconButtonSx = {
        fontSize: 75,
        ml: 2,
        mr: 2
    };

    const linkSx = {
        color: "#1976d2"
    };

    const stackSx = {
        mb: 10
    };

    const typographySx = {
        mb: 5,
        textAlign: "center"
    };

    const breadcrumbs = [
        <Link key="1" underline="hover" color="inherit" href="/add-reminder" sx={linkSx}>
            Name & Dose
        </Link>,
        <Typography key="2" color="text.primary">
            Days of Week
        </Typography>
    ];

    const {handleSubmit, control, formState} = useForm({
        mode: "onChange",
        resolver: yupResolver(validationSchema)
    });

    // An outline of this solution was provided to me here: https://stackoverflow.com/questions/69876501/disable-options-based-on-currently-selected-option-with-material-ui-autocomplete
    const [daysOfWeekSuggestions, setOptions] = useState(initOptions);
    const [selected, setSelected] = useState([]);

    const onChangeDisabled = (e, valueArr) => {
        setSelected(valueArr);

        if (valueArr.length === 0) {
            const resetOptions = [...daysOfWeekSuggestions].map((opt) => {
                opt.disabled = false;
                return opt;
            });
            setOptions(resetOptions);
        } else {
            const everyDaySelected = valueArr.find(
                (item) => item.label === "Every day"
            );

            if (everyDaySelected) {
                const updatedOptions = [...daysOfWeekSuggestions].map((opt) => {
                    opt.disabled = opt.label !== "Every day";
                    return opt;
                });
                setOptions(updatedOptions);
            } else {
                const updatedOptions = [...daysOfWeekSuggestions].map((opt) => {
                    opt.disabled = opt.label === "Every day";
                    return opt;
                });
                setOptions(updatedOptions);
            }
        }
    };

    const onSubmit = (data) => {
        // This saves the reminder into a local session storage for the purposes of displaying the reminders on another page.
        // If I were going to create a deployed version of the app, I would create a DB and save the data there.
        // I also set this condition --> localStorage.getItem("reminders").length < 2 --> because if the array is empty, the length === 2
        if (localStorage.getItem("reminders") === null || localStorage.getItem("reminders").length < 3) {
            localStorage.setItem("reminders", JSON.stringify([{
                id: 0,
                medication: "",
                dose: "",
                days: [],
                timesPerDay: 0,
                times: []
            }]));
        }

        mostRecent.days = daysArr;
        parsed[parsed.length - 1] = mostRecent;

        localStorage.setItem("reminders", JSON.stringify(parsed));

        /*
        fetch("http://localhost:3000/add-reminder/days/nums")
            .then((resp) => {
                window.location = "http://localhost:3000/add-reminder/days/nums";
            });
         */
    };

    return (
        <Container>
            <Stack spacing={2} sx={stackSx}>
                <Breadcrumbs
                    separator={<NavigateNextIcon fontSize="small"/>}
                    aria-label="breadcrumb"
                >
                    {breadcrumbs}
                </Breadcrumbs>
            </Stack>

            <Typography sx={typographySx} variant="h4">
                Which days of the week do you take your medication?
            </Typography>

            <form onSubmit={handleSubmit(onSubmit)} noValidate>
                <Box sx={boxSx}>
                    <Controller
                        name="daysOfWeek"
                        control={control}
                        defaultValue={[]}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                autoHighlight
                                disableClearable
                                disablePortal
                                filterSelectedOptions
                                multiple
                                getOptionDisabled={(option) => !!option.disabled}
                                id="days-autocomplete"
                                onChange={(event, value) => {
                                    field.onChange(value);
                                    onChangeDisabled(event, value);
                                }}
                                options={daysOfWeekSuggestions}
                                sx={autoCompleteSx}
                                value={selected}
                                renderInput={(params) => (
                                    <TextField
                                        required
                                        error={!!error}
                                        helperText={error?.message}
                                        id="daysOfWeek"
                                        label="Days of the week"
                                        name="daysOfWeek"
                                        type="search"
                                        inputRef={ref}
                                        {...params}
                                    />
                                )}
                            />
                        )}
                    />

                    <Box>
                        <Button href="/add-reminder"><ArrowBackIcon sx={iconButtonSx}/></Button>

                        <Button disabled={!formState.isValid}
                                type="submit"><ArrowForwardIcon sx={iconButtonSx}/></Button>
                    </Box>
                </Box>
            </form>
        </Container>
    );
}
