import React, {useState} from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";

const defaultValues = {
    medication: "",
    dose: 0,
};

export default function MedicationInput() {
    const [formValues, setFormValues] = useState(defaultValues);

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setFormValues({
            ...formValues,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formValues);
    };

    return (
        <form onSubmit={handleSubmit}>
            <Box
                sx={{
                    "& .MuiTextField-root": {mb: 5, width: "100%"},
                }}
                noValidate
                autoComplete="on"
            >
                <div>
                    <TextField
                        required
                        id="medicationInput"
                        name="medicationInput"
                        label="Medication"
                        type="search"
                        helperText="Input your medication's name"
                        value={formValues.medication}
                        onChange={handleInputChange}
                    />
                    <br/>
                    <TextField
                        required
                        id="doseInput"
                        name="doseInput"
                        label="Dose"
                        type="number"
                        helperText="Input your medication's dosage in mg"
                        value={formValues.dose}
                        onChange={handleInputChange}
                    />
                </div>

                <Button variant="contained" size="large" type="submit"
                        sx={{fontSize: "17px", height: "5.75ch", width: "100%"}}>Submit</Button>
            </Box>
        </form>
    );
}
