import * as React from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import IconButton from '@mui/material/IconButton';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

// Custom Components
import NoRegisteredMedications from "./NoRegisteredMedications";

const validationSchema = Yup.object().shape({
    medication: Yup.string()
        .required("Medication is required"),
    dose: Yup.string()
        .required("Dose is required"),
});

function setSuggestions() {
    // This is the array of medication objects that will be displayed on the cards
    let medicationList = localStorage.getItem("medications");
    let parsed = JSON.parse(medicationList);

    // This will be an array of medication suggestions for the inputs
    let medSuggestions = [];
    let doseSuggestions = [];

    if (parsed !== null && parsed.length > 0) {
        parsed.forEach((medication) => {
            let medObject = {label: medication.medication};
            let doseObject = {label: medication.dose};
            medSuggestions.push(medObject);
            doseSuggestions.push(doseObject);
        });

        return [medSuggestions, doseSuggestions];
    }

    return [];
}

export default function AddReminder1() {
    // Styles
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const {handleSubmit, control, formState} = useForm({
        mode: "onChange",
        resolver: yupResolver(validationSchema)
    });

    const onSubmit = (data) => {
        console.log(data);
    };

    if (setSuggestions().length > 1) {
        return (
            <Container>
                <form onSubmit={handleSubmit(onSubmit)} noValidate>
                    <Box
                        sx={boxSx}
                    >
                        <Controller
                            name="medication"
                            control={control}
                            defaultValue={""}
                            render={({field: {ref, ...field}, fieldState: {error}}) => (
                                <Autocomplete
                                    {...field}
                                    autoHighlight
                                    disableClearable
                                    isOptionEqualToValue={(option, value) => option.id === value.id}
                                    id="medication-autocomplete"
                                    onChange={(event, value) => field.onChange(value.label)}
                                    options={setSuggestions()[0]}
                                    renderInput={(params) => (
                                        <TextField
                                            required
                                            error={!!error}
                                            helperText={error?.message}
                                            id="medication"
                                            label="Medication"
                                            name="medication"
                                            type="search"
                                            inputRef={ref}
                                            {...params}
                                        />
                                    )}
                                />
                            )}
                        />

                        <Controller
                            name="dose"
                            control={control}
                            defaultValue={""}
                            render={({field: {ref, ...field}, fieldState: {error}}) => (
                                <Autocomplete
                                    {...field}
                                    autoHighlight
                                    disableClearable
                                    isOptionEqualToValue={(option, value) => option.id === value.id}
                                    id="dose-autocomplete"
                                    onChange={(event, value) => field.onChange(value.label)}
                                    options={setSuggestions()[1]}
                                    renderInput={(params) => (
                                        <TextField
                                            required
                                            error={!!error}
                                            helperText={error?.message}
                                            id="dose"
                                            label="Dose"
                                            name="dose"
                                            type="numeric"
                                            inputRef={ref}
                                            {...params}
                                        />
                                    )}
                                />
                            )}
                        />

                        <IconButton disabled={!formState.isValid} size="large"
                                    type="submit"><ArrowForwardIcon/></IconButton>
                    </Box>
                </form>
            </Container>
        );
    } else {
        return (
            <NoRegisteredMedications/>
        );
    }
}
