import {useState, useEffect} from "react";
import {useForm, Controller} from "react-hook-form";

// Material UI Components
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
import Box from "@mui/material/Box";
import Breadcrumbs from "@mui/material/Breadcrumbs";
import Button from "@mui/material/Button";
import CloseIcon from "@mui/icons-material/Close";
import Container from "@mui/material/Container";
import IconButton from "@mui/material/IconButton";
import Link from "@mui/material/Link";
import LocalizationProvider from "@mui/lab/LocalizationProvider";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import Stack from "@mui/material/Stack";
import TimePicker from "@mui/lab/TimePicker";
import Typography from "@mui/material/Typography";

import DialogTitle from "@mui/material/DialogTitle";
import Dialog from "@mui/material/Dialog";
import TextField from "@mui/material/TextField";
import AdapterDateFns from "@mui/lab/AdapterDateFns";

// This identifies if any of the keys in the reminder DS still have a default value
function getDefaultValues(mostRecent) {
    const flagsObj = {
        medicationFlag: true,
        doseFlag: true,
        daysFlag: true,
        timesPerDayFlag: true,
        timesFlag: true
    }

    if (mostRecent.medication === "") {
        flagsObj.medicationFlag = false;
    }

    if (mostRecent.dose === "") {
        flagsObj.doseFlag = false;
    }

    if (mostRecent.days.length === 0) {
        flagsObj.daysFlag = false;
    }

    if (mostRecent.timesPerDay === "") {
        flagsObj.timesPerDayFlag = false;
    }

    if (mostRecent.times.length === 0) {
        flagsObj.timesFlag = false;
    }


    const filtered = Object.entries(flagsObj).filter(([key, value]) => {
        let keyReturn = "";

        if (value === false) {
            keyReturn = key.toString();
        }

        return keyReturn; // returns an array in the form ["key", "value"}
    });

    return filtered.map((arr) => arr[0]);
}

// This gets the number of timePicker inputs that need to be created
function getNumber() {
    return getMostRecent().timesPerDay;
}

function getMostRecent() {
    const parsed = getParsed();
    return parsed[parsed.length - 1];
}

function getParsed() {
    const reminderList = localStorage.getItem("reminders");
    return JSON.parse(reminderList);
}

function getTimes(timeString) {
    const times = [];

    Object.values(timeString).forEach((value) => {
        let val = value.toString();
        times.push(val.slice(16, 24));
    });

    return (times);
}

export default function AddReminder4() {
    // Styles
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        alignItems: "center",
        bgcolor: "background.paper",
        display: "flex",
        flexDirection: "column",
        ml: "50px",
        mr: "50px",
    };

    const dialogTitleSx = {
        m: 0,
        p: 3
    };

    const iconButtonSx = {
        fontSize: 75,
        ml: 2.5,
        mr: 2.5
    };

    const linkSx = {
        color: "#1976d2"
    };

    const stackSx = {
        mb: 5
    };

    const breadcrumbs = [
        <Link key="1" underline="hover" color="inherit" href="/add-reminder" sx={linkSx}>
            Name & Dose
        </Link>,
        <Link key="2" underline="hover" color="inherit" href="/add-reminder/days" sx={linkSx}>
            Days of Week
        </Link>,
        <Link key="3" underline="hover" color="inherit" href="/add-reminder/days/nums" sx={linkSx}>
            # of Times
        </Link>,
        <Typography key="4" color="text.primary">
            Times
        </Typography>
    ];

    const Error = () => {
        const [open, setOpen] = useState(false);

        const handleOpen = () => {
            setOpen(true);
        };

        const handleClose = () => {
            setOpen(false);
        };

        useEffect(() => {
            handleOpen();
        }, []);

        return (
            <Box>
                <Dialog
                    keepMounted={true}
                    open={open}
                    onClose={handleClose}
                >
                    <DialogTitle sx={dialogTitleSx}>
                        {handleClose ? (
                            <IconButton
                                aria-label="close"
                                onClick={handleClose}
                                sx={iconButtonSx}
                            >
                                <CloseIcon/>
                            </IconButton>
                        ) : null}
                    </DialogTitle>

                    TEST

                </Dialog>
            </Box>
        );
    };

    // This solution was inspired by this tutorial: https://rexebin.medium.com/use-material-ui-pickers-with-react-hook-form-3ed5d24d6afc
    const TimeInputs = (params) => {
        let name = "Time " + params.counter;

        const [time, setTime] = useState(new Date("2000-01-01T00:00:00"));
        const value = getValues(name);

        useEffect(() => {
            register(name);
        }, [name]);
        useEffect(() => {
            setTime(value || null);
        }, [setTime, value]);

        return (
            <Controller
                control={control}
                name={name}
                defaultValue={time}
                render={({field: {ref, ...field}}) => (
                    <TimePicker
                        {...field}
                        label={name}
                        value={time}
                        onChange={(time) => {
                            setValue(name, time, {shouldValidate: true, shouldDirty: true});
                            setTime(time);
                        }}
                        renderInput={(params) =>
                            <TextField
                                id={name}
                                name={name}
                                inputRef={ref}
                                {...params}
                            />
                        }
                    />
                )}
            />
        );
    };

    const allTimes = [];

    for (let i = 0; i < getNumber(); i++) {
        allTimes.push(<TimeInputs counter={i + 1} key={"TimeInputs" + i}/>);
    }

    const [, isValid] = useState(true);
    
    const onSubmit = (data) => {
        if (localStorage.getItem("reminders") === null || localStorage.getItem("reminders").length < 3) {
            localStorage.setItem("reminders", JSON.stringify([{
                id: 0,
                medication: "",
                dose: "",
                days: [],
                timesPerDay: 0,
                times: [],
                submitted: false
            }]));
        }

        let mostRecent = getMostRecent();
        let defaultValues = getDefaultValues(mostRecent);
        let parsed = getParsed();

        if (defaultValues.length > 0 && mostRecent.submitted === true) {
            isValid(false);
        }

        mostRecent.times = getTimes(data);
        mostRecent.submitted = true;
        parsed[parsed.length - 1] = mostRecent;
    };

    const {control, handleSubmit, setValue, register, getValues} = useForm();

    return (
        <Container>
            <Stack spacing={2} sx={stackSx}>
                <Breadcrumbs
                    separator={<NavigateNextIcon fontSize="small"/>}
                    aria-label="breadcrumb"
                >
                    {breadcrumbs}
                </Breadcrumbs>
            </Stack>

            <form onSubmit={handleSubmit(onSubmit)}>
                <Box sx={boxSx}>
                    <LocalizationProvider dateAdapter={AdapterDateFns}>
                        {allTimes}
                    </LocalizationProvider>

                    {!isValid ? <Error/> : null}

                    <Box>
                        <Button href="/add-reminder/days/nums"><ArrowBackIcon sx={iconButtonSx}/></Button>

                        <Button type="submit"><ArrowForwardIcon sx={iconButtonSx}/></Button>
                    </Box>
                </Box>
            </form>
        </Container>
    );
}
