import {useState, useCallback, forwardRef} from "react";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";

import Dialog from "@mui/material/Dialog";
import Slide from "@mui/material/Slide";

const Transition = forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

export default function DisplayMedications() {
    // This handles the delete medication confirmation modal
    const [open, setOpen] = useState(false);
    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };

    // This handles the delete medication function
    const [, updateState] = useState();
    const forceUpdate = useCallback(() => updateState({}), []);

    function deleteMedication(medicationId, parsedMedications) {
        if (parsedMedications.length > 1) {
            parsedMedications.splice(medicationId, 1);
        } else {
            parsedMedications = [];
        }

        localStorage.setItem("medications", JSON.stringify(parsedMedications));
        forceUpdate();
    }

    const buttonSx = {
        borderRadius: 0,
        fontSize: "17px",
        height: "5.75ch",
        width: "50%"
    };

    const cardSx = {
        bgcolor: "#eaeef3",
        boxShadow: 3,
        m: 5
    };

    const typographyMedicationSx = {
        textAlign: "left"
    };

    let medications;

    // I also set this condition --> localStorage.getItem("medications").length < 2 --> because if the array is empty, the length === 2
    if (localStorage.getItem("medications") !== null && localStorage.getItem("medications").length > 2) {
        let medicationList = localStorage.getItem("medications");
        let parsed = JSON.parse(medicationList);

        medications = parsed.map((medication) => {
            return (
                <Box key={medication.id}>
                    <Card sx={cardSx}>
                        <CardContent>
                            <Typography key={medication.medication.id} sx={typographyMedicationSx} variant="h5">
                                Medication: {medication.medication}
                            </Typography>
                            <Typography key={medication.dose.id} sx={typographyMedicationSx} variant="h5">
                                Dose: {medication.dose} mg
                            </Typography>
                        </CardContent>
                        <Box>
                            <Button size="large" sx={buttonSx} variant="contained">Edit Dose</Button>
                            <Button onClick={handleClickOpen} color="error"
                                    size="large"
                                    sx={buttonSx} variant="contained">Delete
                                Med</Button>
                        </Box>
                    </Card>

                    <Box>
                        <Dialog
                            open={open}
                            onClose={handleClose}
                            TransitionComponent={Transition}
                        >
                            <Typography variant="h5" sx={{m: 5}}> Are you sure you want to delete the
                                medication? </Typography>

                            <Box>
                                <Button size="large" sx={buttonSx} variant="contained">Yes</Button>
                                <Button onClick={() => deleteMedication(medication.id, parsed)} color="error"
                                        size="large"
                                        sx={buttonSx} variant="contained">No</Button>
                            </Box>
                        </Dialog>
                    </Box>
                </Box>
            )
        });
    } else {
        medications =
            <Card sx={cardSx} variant="outlined">
                <CardContent>
                    <Typography sx={typographyMedicationSx} variant="h5">
                        No medications are registered.
                    </Typography>
                </CardContent>
                <Box>
                    <Button href="/add-medication" size="large" sx={Object.assign(buttonSx, {width: "100%"})}
                            variant="contained">Register
                        med</Button>
                </Box>
            </Card>
        ;
    }

    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                {medications}
            </Box>
        </Container>
    )
}
