import * as React from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

const medicationSuggestions = [
    {label: "Lisinopril"},
    {label: "Valsartan"}
];

const validationSchema = Yup.object().shape({
    medication: Yup.string()
        .required("Medication is required"),
});

export default function RegisterReminder1() {
    // This is the array of medication objects that will be displayed on the cards
    let medicationList = localStorage.getItem("medications");
    let parsed = JSON.parse(medicationList);

    if (parsed.length > 0) {
        for (let i = 0; i < parsed.length; i++) {
            console.log(parsed);
        }
    }

    // Styles
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const {handleSubmit, control, formState} = useForm({
        mode: "onChange",
        resolver: yupResolver(validationSchema)
    });

    const onSubmit = (data) => {
        console.log(data);
    };

    return (
        <Container>
            <form onSubmit={handleSubmit(onSubmit)} noValidate>
                <Box
                    sx={boxSx}
                >
                    <Controller
                        name="medication"
                        control={control}
                        defaultValue={""}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                autoHighlight
                                disableClearable
                                isOptionEqualToValue={(option, value) => option.id === value.id}
                                id="medication-autocomplete"
                                onChange={(event, value) => field.onChange(value.label)}
                                options={medicationSuggestions}
                                renderInput={(params) => (
                                    <TextField
                                        required
                                        error={!!error}
                                        helperText={error?.message}
                                        id="medication"
                                        label="Medication"
                                        name="medication"
                                        type="search"
                                        inputRef={ref}
                                        {...params}
                                    />
                                )}
                            />
                        )}
                    />

                    <Button disabled={!formState.isValid} size="large" sx={buttonSx} type="submit"
                            variant="contained">Submit</Button>
                </Box>
            </form>
        </Container>
    );
}
