import * as React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";
import {useForm} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

export default function MedicationInput() {
    const validationSchema = Yup.object().shape({
        medication: Yup.string()
            .required("Medication name is required"),
        dose: Yup.string()
            .required("Dosage is required")
            .min(1, "Dose must be at least 1 mg")
    });

    const {
        register,
        handleSubmit,
        formState: {errors}
    } = useForm({
        resolver: yupResolver(validationSchema)
    });

    const onSubmit = data => {
        console.log(JSON.stringify(data, null, 2));
    };

    return (
        <Box
            component="form"
            sx={{
                "& .MuiTextField-root": {mb: 5, width: "100%"},
            }}
            noValidate
            autoComplete="on"
        >
            <div>
                <TextField
                    required
                    id="medicationInput"
                    name="medicationInput"
                    label="Medication"
                    type="search"
                    helperText="Input your medication's name"
                    {...register("medicationInput")}
                    error={errors.medicationInput ? true : false}
                />
                <Typography variant="inherit" color="textSecondary">
                    {errors.medicationInput?.message}
                </Typography>
                <br/>
                <TextField
                    required
                    id="doseInput"
                    name="doseInput"
                    label="Dose"
                    type="number"
                    helperText="Input your medication's dosage in mg"
                    {...register("doseInput")}
                    error={errors.doseInput ? true : false}
                />
                <Typography variant="inherit" color="textSecondary">
                    {errors.doseInput?.message}
                </Typography>
            </div>

            <Button variant="contained" size="large" sx={{fontSize: "17px", height: "5.75ch", width: "100%"}}
                    onClick={handleSubmit(onSubmit)}> Submit</Button>
        </Box>
    );
}
