import * as React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import Container from "@mui/material/Container";
import Link from "@mui/material/Link";
import Typography from "@mui/material/Typography";
import PillLogo from "./pill.svg";
import TextField from "@mui/material/TextField";

import {useForm} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

export default function Login() {
    // Styles and SX
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        bgcolor: "background.paper"
    }

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        mb: 5,
        width: "100%"
    };

    const containerDivStyle = {
        alignItems: "center",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center"
    };

    const cardSx = {
        bgcolor: "background.paper",
        ml: 5,
        mt: 5,
        mr: 5,
        p: 5
    };

    const imgStyle = {
        filter: "invert(100%) sepia(0%) saturate(0%) hue-rotate(231deg) brightness(103%) contrast(101%)",
        height: 250
    };

    let isValid = false;

    // Form onSubmit
    const onSubmit = data => {
        const values = getValues();
        isValid = values.username === "test" && values.password === "pass";

        if (!isValid) {
            document.getElementById("userPassError").style.display = "flex";
        }

        console.log(JSON.stringify(data, null, 2));
    };

    // Form validation variables
    const validationSchema = Yup.object().shape({
        username: Yup.string()
            .required("Username is required"),
        password: Yup.string()
            .required("Password is required")
    });

    const {
        formState: {errors},
        handleSubmit,
        register,
        getValues
    } = useForm({
        resolver: yupResolver(validationSchema),
    });

    return (
        <Container>
            <Box sx={containerDivStyle}>
                <img alt="Pill Logo" src={PillLogo} style={imgStyle}/>
            </Box>

            <Card variant="outlined" sx={cardSx}>
                <form>
                    <Box
                        sx={boxSx}
                    >
                        <TextField
                            required
                            id="username"
                            label="Username"
                            name="username"
                            {...register("username")}
                            error={errors.username === false || isValid === false}
                        />

                        <Typography color="textSecondary" variant="inherit">
                            {errors.username?.message}
                        </Typography>

                        <TextField
                            required
                            id="password"
                            label="Password"
                            name="password"
                            {...register("password")}
                            error={(errors.username ? true : false) || isValid}
                        />

                        <Typography color="textSecondary" variant="inherit">
                            {errors.password?.message}
                        </Typography>

                        <Button onClick={handleSubmit(onSubmit)} size="large" sx={buttonSx} type="submit"
                                variant="contained">Login</Button>

                        <Typography color="textSecondary" id="userPassError" style={{display: "none"}}
                                    variant="inherit">
                            Error!!!!
                        </Typography>
                    </Box>
                </form>

                <Link href="#" underline="hover">
                    {"Create an account"}
                </Link>
            </Card>
        </Container>
    );
}
