import React, {useState} from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

const initDaysOptions = [
    {label: "Every day", disabled: false},
    {label: "Sunday", disabled: false},
    {label: "Monday", disabled: false},
    {label: "Tuesday", disabled: false},
    {label: "Wednesday", disabled: false},
    {label: "Thursday", disabled: false},
    {label: "Friday", disabled: false},
    {label: "Saturday", disabled: false}
];

const medicationSuggestions = [
    {label: "Lisinopril"},
    {label: "Valsartan"}
];

export default function RegisterReminder() {
    // This declares the state variables
    const [daysOfWeekSuggestions, setOptions] = useState(initDaysOptions);
    const [selected, setSelected] = useState([]);

    //Styles and SX
    const boxSx = {
        "& .MuiTextField-root": {mb: 2.5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const errorSx = {
        mb: 2.5
    };

    // Form validation using Yup
    const validationSchema = Yup.object().shape({
        medication: Yup.string()
            .required("Medication is required"),
        daysOfWeek: Yup.array()
            .of(
                Yup.object().shape({
                    value: Yup.string(),
                    label: Yup.string()
                })
            )
            .min(1, "Days of the week are required")
    });

    // React Hook Form formState and useForm
    const {
        formState: {errors},
        handleSubmit,
        control,
        register
    } = useForm({
        resolver: yupResolver(validationSchema)
    });

    // This makes sure the user can't choose both "Every day" and days of the week, but they can still manually choose every day
    const onOptionsChange = (event, valueArr) => {
        setSelected(valueArr);
        if (valueArr.length === 0) {
            const resetOptions = [...daysOfWeekSuggestions].map((opt) => {
                opt.disabled = false;
                return opt;
            });
            setOptions(resetOptions);
        } else {
            const everyDaySelected = valueArr.find(
                (item) => item.label === "Every day"
            );

            const notEveryDaySelected = valueArr.find(
                (item) => item.label !== "Every day"
            );

            if (everyDaySelected) {
                const updatedOptions = [...daysOfWeekSuggestions].map((opt) => {
                    opt.disabled = opt.label !== "Every day";
                    return opt;
                });
                setOptions(updatedOptions);
            }

            if (notEveryDaySelected) {
                const updatedOptions = [...daysOfWeekSuggestions].map((opt) => {
                    opt.disabled = opt.label === "Every day";
                    return opt;
                });
                setOptions(updatedOptions);
            }
        }
    };

    // Form onSubmit
    const onSubmit = (data) => {
        console.log(data);
    };

    return (
        <Container>
            <form>
                <Box
                    sx={boxSx}
                >

                    <Autocomplete
                        disableClearable
                        disablePortal
                        id="medication-autocomplete"
                        options={medicationSuggestions}
                        renderInput={(params) => <TextField
                            required
                            error={errors.medication ? true : false}
                            id="medication"
                            label="Medication"
                            name="medication"
                            type="search"
                            {...params}
                            {...register("medication")}
                        />}
                    />

                    <Typography color="error" sx={errorSx} variant="inherit">
                        {errors.medication?.message}
                    </Typography>

                    <Controller
                        name="daysOfWeek"
                        control={control}
                        defaultValue={[]}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                disableClearable
                                disablePortal
                                filterSelectedOptions
                                multiple
                                getOptionDisabled={(option) => option.disabled ? true : false}
                                getOptionLabel={(option) => option.label}
                                id="days-autocomplete"
                                onChange={(event, value) => onOptionsChange(event, value)}
                                options={daysOfWeekSuggestions}
                                renderInput={(params) => <TextField
                                    required
                                    error={errors.daysOfWeek ? true : false}
                                    id="daysOfWeek"
                                    label="Days of the week"
                                    name="daysOfWeek"
                                    inputRef={ref}
                                    type="search"
                                    {...params}
                                />}
                                value={selected}
                            />
                        )}
                    />

                    <Typography color="error" sx={errorSx} variant="inherit">
                        {errors.daysOfWeek?.message}
                    </Typography>

                    <Button onClick={handleSubmit(onSubmit)} size="large" sx={buttonSx} type="submit"
                            variant="contained">Submit</Button>
                </Box>
            </form>
        </Container>
    );
}
