import * as React from "react";
import {useForm} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import MenuItem from "@mui/material/MenuItem";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";

import PropTypes from "prop-types";
import Autosuggest from "react-autosuggest";
import match from "autosuggest-highlight/match";
import parse from "autosuggest-highlight/parse";

const medicationSuggestions = [
    {label: "Lisinopril"},
    {label: "Afghanistan"}
];

const doseSuggesions = [
    {label: 10},
    {label: 20},
    {label: 30},
    {label: 40},
    {label: 50},
    {label: 60},
    {label: 70},
    {label: 80}
];

function renderSuggestion(suggestion, {query, isHighlighted}) {
    const matches = match(suggestion.label, query);
    const parts = parse(suggestion.label, matches);

    return (
        <MenuItem selected={isHighlighted} component="div">
            <div>
                {parts.map((part, index) => {
                    return part.highlight ? (
                        <span key={String(index)} style={{fontWeight: 500}}>
              {part.text}
            </span>
                    ) : (
                        <strong key={String(index)} style={{fontWeight: 300}}>
                            {part.text}
                        </strong>
                    );
                })}
            </div>
        </MenuItem>
    );
}

export default function MedicationInput() {
    //Styles and SX
    const boxSx = {
        "& .MuiTextField-root": {mb: 2.5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const errorSx = {
        mb: 2.5
    };

    // Form validation using Yup
    const validationSchema = Yup.object().shape({
        medication: Yup.string()
            .required("Medication is required"),
        dose: Yup.string()
            .required("Dose is required")
    });

    // Form onSubmit
    const onSubmit = (data) => {
        // This is implemented so the error UI for an invalid username/password can be displayed
        console.log(data);
    };

    const {
        formState: {errors},
        handleSubmit,
        register
    } = useForm({
        resolver: yupResolver(validationSchema),
    });

    return (
        <Container>
            <form>
                <Box
                    sx={boxSx}
                >
                    <TextField
                        required
                        error={errors.medication ? true : false}
                        id="medication"
                        label="Medication"
                        name="medication"
                        type="search"
                        {...register("medication")}
                    />

                    <Typography color="error" sx={errorSx} variant="inherit">
                        {errors.medication?.message}
                    </Typography>

                    <TextField
                        required
                        error={errors.dose ? true : false}
                        id="dose"
                        label="Dose"
                        name="dose"
                        type="numeric"
                        {...register("dose")}
                    />

                    <Typography color="error" sx={errorSx} variant="inherit">
                        {errors.dose?.message}
                    </Typography>

                    <Button onClick={handleSubmit(onSubmit)} size="large" sx={buttonSx} type="submit"
                            variant="contained">Submit</Button>
                </Box>
            </form>
        </Container>
    );
}
