import {useState, useCallback} from "react";

// Material UI Components
import AppBar from '@mui/material/AppBar';
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from '@mui/material/CardContent';
import Container from "@mui/material/Container";
import Dialog from '@mui/material/Dialog';
import Slide from '@mui/material/Slide';
import Toolbar from '@mui/material/Toolbar';
import Typography from "@mui/material/Typography";

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

export default function DisplayMedications() {
    const [, updateState] = useState();
    const forceUpdate = useCallback(() => updateState({}), []);

    const [open, setOpen] = useState(false);
    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };

    function confirmDelete(medicationId, parsedMedications) {
        return (
            <div>
                <Button variant="outlined" onClick={handleClickOpen}>
                    Open full-screen dialog
                </Button>
                <Dialog
                    fullScreen
                    open={open}
                    onClose={handleClose}
                    TransitionComponent={Transition}
                >
                    <AppBar sx={{position: 'relative'}}>
                        <Toolbar>
                            <Typography sx={{ml: 2, flex: 1}} variant="h6" component="div">
                                Confirm Deletion
                            </Typography>
                            <IconButton
                                edge="start"
                                color="inherit"
                                onClick={handleClose}
                                aria-label="close"
                            >
                                <CloseIcon/>
                            </IconButton>
                        </Toolbar>
                    </AppBar>

                    <Typography variant="h5"> Are you sure you want to delete the medication? </Typography>

                </Dialog>
            </div>
        )
    }

    function deleteMedication(medicationId, parsedMedications) {
        if (parsedMedications.length > 1) {
            parsedMedications.splice(medicationId, 1);
        } else {
            parsedMedications = [];
        }

        localStorage.setItem("medications", JSON.stringify(parsedMedications));
        forceUpdate();
    }

    const buttonSx = {
        borderRadius: 0,
        fontSize: "17px",
        height: "5.75ch",
        width: "50%"
    };

    const cardSx = {
        bgcolor: "#eaeef3",
        boxShadow: 3,
        m: 5
    };

    const typographyMedicationSx = {
        textAlign: "left"
    };

    let medications;

    // I also set this condition --> localStorage.getItem("medications").length < 2 --> because if the array is empty, the length === 2
    if (localStorage.getItem("medications") !== null && localStorage.getItem("medications").length > 2) {
        let medicationList = localStorage.getItem("medications");
        let parsed = JSON.parse(medicationList);

        medications = parsed.map((medication) => {
            return (
                <Card key={medication.id} sx={cardSx}>
                    <CardContent>
                        <Typography key={medication.medication.id} sx={typographyMedicationSx} variant="h5">
                            Medication: {medication.medication}
                        </Typography>
                        <Typography key={medication.dose.id} sx={typographyMedicationSx} variant="h5">
                            Dose: {medication.dose} mg
                        </Typography>
                    </CardContent>
                    <Box>
                        <Button size="large" sx={buttonSx} variant="contained">Edit Dose</Button>
                        <Button onClick={() => deleteMedication(medication.id, parsed)} color="error"
                                size="large"
                                sx={buttonSx} variant="contained">Delete
                            Med</Button>
                    </Box>
                </Card>
            )
        });
    } else {
        medications =
            <Card sx={cardSx} variant="outlined">
                <CardContent>
                    <Typography sx={typographyMedicationSx} variant="h5">
                        No medications are registered.
                    </Typography>
                </CardContent>
                <Box>
                    <Button href="/add-medication" size="large" sx={Object.assign(buttonSx, {width: "100%"})}
                            variant="contained">Register
                        med</Button>
                </Box>
            </Card>
        ;
    }

    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                {medications}
            </Box>
        </Container>
    )
}
