import * as React from "react";
import {useForm} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

const daysOfWeekSuggestions = [
    {label: "Every day"},
    {label: "Sunday"},
    {label: "Monday"},
    {label: "Tuesday"},
    {label: "Wednesday"},
    {label: "Thursday"},
    {label: "Friday"},
    {label: "Saturday"}
];

const medicationSuggestions = [
    {label: "Lisinopril"},
    {label: "Valsartan"}
];

export default function RegisterReminder() {
    //Styles and SX
    const boxSx = {
        "& .MuiTextField-root": {mb: 2.5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const errorSx = {
        mb: 2.5
    };

    // Form validation using Yup
    const validationSchema = Yup.object().shape({
        medication: Yup.string()
            .required("Medication is required"),
        daysOfWeek: Yup.string()
            .required("Days of the week are required")
    });

    // Form onSubmit
    const onSubmit = (data) => {
        console.log(data);
    };

    const {
        formState: {errors},
        handleSubmit,
        register
    } = useForm({
        resolver: yupResolver(validationSchema),
    });

    const [value, setValue] = React.useState([]);

    const handleOnChange = (e, value) => {
        setValue(value);
    };

    return (
        <Container>
            <form>
                <Box
                    sx={boxSx}
                >

                    <Autocomplete
                        disableClearable
                        disablePortal
                        id="medication-autocomplete"
                        options={medicationSuggestions}
                        renderInput={(params) => <TextField
                            required
                            error={errors.medication ? true : false}
                            id="medication"
                            label="Medication"
                            name="medication"
                            type="search"
                            {...params}
                            {...register("medication")}
                        />}
                    />

                    <Typography color="error" sx={errorSx} variant="inherit">
                        {errors.medication?.message}
                    </Typography>

                    <Autocomplete
                        disableClearable
                        disablePortal
                        filterSelectedOptions
                        multiple
                        getOptionDisabled={(option) => {
                            if (value.some((day) => day.label === "Every day")) {
                                return true;
                            }
                            if (value.some((day) => day.label === option.label)) {
                                return true;
                            }
                        }}
                        getOptionLabel={(option) => option.label}
                        id="days-autocomplete"
                        options={daysOfWeekSuggestions}
                        renderInput={(params) => <TextField
                            required
                            error={errors.daysOfWeek ? true : false}
                            id="daysOfWeek"
                            label="Days of the week"
                            name="daysOfWeek"
                            type="search"
                            {...params}
                            {...register("daysOfWeek")}
                        />}
                        value={value}
                    />

                    <Typography color="error" sx={errorSx} variant="inherit">
                        {errors.daysOfWeek?.message}
                    </Typography>

                    <Button onClick={handleSubmit(onSubmit)} size="large" sx={buttonSx} type="submit"
                            variant="contained">Submit</Button>
                </Box>
            </form>
        </Container>
    );
}
