import * as React from "react";

// Material UI Components
import AddIcon from "@mui/icons-material/AddCircle";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";
import Chip from "@mui/material/Chip";
import {useCallback, useState} from "react";

export default function DisplayReminders() {
    // Styles
    const cardSx = {
        bgcolor: "#eeeeee",
        boxShadow: 3,
        m: 5,
        pb: 0,
        pl: 1,
        pr: 1,
        pt: 0
    };

    const chipSx = {
        fontSize: 17,
        mb: 0.75,
        mr: 1.5,
        mt: 0.75
    };

    const typographySx = {
        mb: 1.5,
        mt: 1.5
    };

    // This is the array of reminder objects that will be displayed on the cards
    let reminderList = localStorage.getItem("reminders");
    let parsed = JSON.parse(reminderList);

    // This forces the component to re-render after a medication has been deleted
    const [, updateState] = useState();
    const forceUpdate = useCallback(() => updateState({}), []);

    const ChipList = (params) => {
        return (params.arr.map((index, i) => {
            const handleDelete = () => {
                params.arr.splice(index, 1);

                const reminderList = localStorage.getItem("reminders");
                const parsed = JSON.parse(reminderList);

                for (let i = 0; i < parsed.length; i++) {
                    if (parsed[i].id === params.id) {
                        parsed[i][params.objKey] = params.arr;
                    }
                }

                localStorage.setItem("reminders", JSON.stringify(parsed));
                forceUpdate();
            };

            return (<Chip onDelete={handleDelete} sx={chipSx} key={"Chip" + i} label={index}/>);
        }));
    };

    // This is the Medication component that is displayed including a card, medication info, buttons, and dialogs
    const Reminder = ({reminder}) => {
        // This is what is rendered for the Reminder component
        const handleAddDay = () => {

        };

        const handleAddTime = () => {

        };

        return (
            <Box>
                <Card sx={cardSx}>
                    <CardContent>
                        <Typography sx={typographySx} variant="h5">
                            Medication: {reminder.medication}
                        </Typography>
                        <Typography sx={typographySx} variant="h5">
                            Dose: {reminder.dose} mg
                        </Typography>
                        <Typography variant="h5">
                            Days: <ChipList id={reminder.id} arr={reminder.days} objKey={"days"}/>
                            {/* Note: the onDelete creates the icon in the appropriate spot and there isn't an onAdd option */}
                            <Chip onDelete={handleAddDay} deleteIcon={<AddIcon/>} sx={chipSx} label={"Add day"}/>
                        </Typography>
                        <Typography variant="h5">
                            Times: <ChipList id={reminder.id} arr={reminder.times} objKey={"times"}/>
                            {/* Note: the onDelete creates the icon in the appropriate spot and there isn't an onAdd option */}
                            <Chip color={"primary"} onDelete={handleAddTime} deleteIcon={<AddIcon/>} sx={chipSx} label={"Add time"}/>
                        </Typography>
                    </CardContent>
                </Card>
            </Box>
        )
    }; // End Reminder component

    let reminders;

    // I also set this condition (localStorage.getItem("medications").length > 2) because if the array is empty, the length === 2
    if (localStorage.getItem("medications") !== null && localStorage.getItem("reminders").length > 2) {
        reminders = parsed.map((reminder, index) => {
            return (<Reminder reminder={reminder} key={"reminder" + index}/>)
        });
    } else {
        reminders = <h1>No Reminders</h1>;
    }

    // This is the actual final block of code that is rendered for this component
    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                {reminders}
            </Box>
        </Container>
    );
}
