import {useState, forwardRef} from "react";
import {Controller, useForm} from "react-hook-form";
import * as Yup from "yup";
import {yupResolver} from "@hookform/resolvers/yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CloseIcon from '@mui/icons-material/Close';
import Container from "@mui/material/Container";
import IconButton from '@mui/material/IconButton';
import Typography from "@mui/material/Typography";

import Autocomplete from "@mui/material/Autocomplete";
import Dialog from "@mui/material/Dialog";
import DialogTitle from '@mui/material/DialogTitle';
import Slide from "@mui/material/Slide";
import TextField from "@mui/material/TextField";

const doseSuggestions = [
    {label: "10"},
    {label: "20"},
    {label: "30"},
    {label: "40"},
    {label: "50"},
    {label: "60"},
    {label: "70"},
    {label: "80"}
];

const Transition = forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

// Form validation using Yup
const validationSchema = Yup.object().shape({
    dose: Yup.string()
        .required("Dose is required")
});

export default function DisplayMedications() {
    // This handles the delete medication confirmation modal
    const [open, setOpen] = useState(false);
    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };

    // This handles the update medication modal
    const [openUpdate, setOpenUpdate] = useState(false);
    const handleClickOpenUpdate = () => {
        setOpenUpdate(true);
    };
    const handleCloseUpdate = () => {
        setOpenUpdate(false);
    };

    const {handleSubmit, control, formState} = useForm({
        mode: "onChange",
        resolver: yupResolver(validationSchema)
    });

    function deleteMedication(medicationId, parsedMedications) {
        console.log(medicationId);
        
        if (parsedMedications.length > 1) {
            parsedMedications.splice(medicationId, 1);
        } else {
            parsedMedications = [];
        }

        localStorage.setItem("medications", JSON.stringify(parsedMedications));

        // This forces the dialog to close
        setOpen(false);
    }

    function updateDose(medicationId, parsedMedications, data) {
        console.log(medicationId);
        console.log(data);

        parsedMedications[medicationId].dose = data.dose;
        localStorage.setItem("medications", JSON.stringify(parsedMedications));

        // This forces the dialog to close
        setOpenUpdate(false);
    }

    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        borderRadius: 0,
        fontSize: "17px",
        height: "5.75ch",
        width: "50%"
    };

    const cardSx = {
        bgcolor: "#eaeef3",
        boxShadow: 3,
        m: 5
    };

    const dialogTitleSx = {
        m: 0,
        p: 3
    };

    const formButtonSx = {
        borderRadius: 0,
        fontSize: "17px",
        height: "5.75ch",
        mb: 5,
        width: "100%"
    };

    const iconButtonSx = {
        position: 'absolute',
        right: 8,
        top: 8,
        color: (theme) => theme.palette.grey[500],
    };

    const typographyMedicationSx = {
        textAlign: "left"
    };

    const updateDoseTypography = {
        mb: 5,
        ml: 5,
        mr: 5
    };

    let medications;

    // I also set this condition --> localStorage.getItem("medications").length < 2 --> because if the array is empty, the length === 2
    if (localStorage.getItem("medications") !== null && localStorage.getItem("medications").length > 2) {
        let medicationList = localStorage.getItem("medications");
        let parsed = JSON.parse(medicationList);

        medications = parsed.map((medication) => {
            return (
                <Box key={medication.id}>
                    <Card sx={cardSx}>
                        <CardContent>
                            <Typography sx={typographyMedicationSx} variant="h5">
                                Medication: {medication.medication}
                            </Typography>
                            <Typography sx={typographyMedicationSx} variant="h5">
                                Dose: {medication.dose} mg
                            </Typography>
                        </CardContent>
                        <Box>
                            <Button onClick={handleClickOpenUpdate} size="large" sx={buttonSx} variant="contained">Edit
                                Dose</Button>
                            <Button onClick={handleClickOpen} color="error"
                                    size="large"
                                    sx={buttonSx} variant="contained">Delete
                                Med</Button>
                        </Box>
                    </Card>

                    {/* Delete medication dialog */}
                    <Box>
                        <Dialog
                            key={medication.id}
                            open={open}
                            onClose={handleClose}
                            TransitionComponent={Transition}
                        >
                            <Typography variant="h5" sx={{m: 5}}> Are you sure you want to delete the
                                medication? </Typography>

                            <Box>
                                <Button onClick={() => deleteMedication(medication.id, parsed)} size="large"
                                        sx={buttonSx} variant="contained">Yes</Button>
                                <Button color="error"
                                        size="large"
                                        sx={buttonSx} variant="contained">No</Button>
                            </Box>
                        </Dialog>
                    </Box>

                    {/* Update medication dialog */}
                    <Box>
                        <Dialog
                            open={openUpdate}
                            onClose={handleCloseUpdate}
                            TransitionComponent={Transition}
                        >
                            <DialogTitle sx={dialogTitleSx}>
                                {handleCloseUpdate ? (
                                    <IconButton
                                        aria-label="close"
                                        onClick={handleCloseUpdate}
                                        sx={iconButtonSx}
                                    >
                                        <CloseIcon/>
                                    </IconButton>
                                ) : null}
                            </DialogTitle>

                            <form
                                onSubmit={handleSubmit((data) => updateDose(medication.id, parsed, data))}
                                noValidate>
                                <Typography sx={updateDoseTypography} variant="h4">
                                    Update dose
                                </Typography>

                                <Box
                                    sx={boxSx}
                                >
                                    <Controller
                                        name="dose"
                                        control={control}
                                        defaultValue={""}
                                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                                            <Autocomplete
                                                {...field}
                                                autoHighlight
                                                disableClearable
                                                isOptionEqualToValue={(option, value) => option.id === value.id}
                                                id="dose-autocomplete"
                                                onChange={(event, value) => field.onChange(value.label)}
                                                options={doseSuggestions}
                                                renderInput={(params) => (
                                                    <TextField
                                                        required
                                                        error={!!error}
                                                        helperText={error?.message}
                                                        id="dose"
                                                        label="Dose"
                                                        name="dose"
                                                        type="numeric"
                                                        inputRef={ref}
                                                        {...params}
                                                    />
                                                )}
                                            />
                                        )}
                                    />

                                    <Button disabled={!formState.isValid} size="large" sx={formButtonSx} type="submit"
                                            variant="contained">Submit</Button>
                                </Box>
                            </form>
                        </Dialog>
                    </Box>
                </Box>
            )
        });
    } else {
        medications =
            (<Card sx={cardSx}>
                <CardContent>
                    <Typography sx={typographyMedicationSx} variant="h5">
                        No medications are registered.
                    </Typography>
                </CardContent>
                <Box>
                    <Button href="/add-medication" size="large" sx={Object.assign(buttonSx, {width: "100%"})}
                            variant="contained">Register
                        med</Button>
                </Box>
            </Card>)
        ;
    }

    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                {medications}
            </Box>
        </Container>
    )
}
