import * as React from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

const doseSuggestions = [
    {label: "10"},
    {label: "20"},
    {label: "30"},
    {label: "40"},
    {label: "50"},
    {label: "60"},
    {label: "70"},
    {label: "80"}
];

const medicationSuggestions = [
    {label: "Lisinopril"},
    {label: "Valsartan"}
];

// Form validation using Yup
const validationSchema = Yup.object().shape({
    medication: Yup.string()
        .required("Medication is required"),
    dose: Yup.string()
        .required("Dose is required")
});

export default function MedicationInput() {
    const onSubmit = (data) => {
        console.log(data);
    };

    const {handleSubmit, control} = useForm({
        resolver: yupResolver(validationSchema)
    });

    //Styles and SX
    const boxSx = {
        "& .MuiTextField-root": {mb: 2.5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    return (
        <Container>
            <form>
                <Box
                    sx={boxSx}
                >

                    <Controller
                        name="medication"
                        control={control}
                        defaultValue={""}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                autoHighlight
                                disableClearable
                                isOptionEqualToValue={(option, value) => option.id === value.id}
                                id="medication-autocomplete"
                                onChange={(event, value) => field.onChange(value.label)}
                                options={medicationSuggestions}
                                renderInput={(params) => (
                                    <TextField
                                        required
                                        error={!!error}
                                        helperText={error?.message}
                                        id="medication"
                                        label="Medication"
                                        name="medication"
                                        type="search"
                                        inputRef={ref}
                                        {...params}
                                    />
                                )}
                            />
                        )}
                    />

                    <Autocomplete
                        disableClearable
                        disablePortal
                        id="dose-autocomplete"
                        options={doseSuggestions}
                        renderInput={(params) => <TextField
                            required
                            error={errors.dose ? true : false}
                            id="dose"
                            label="Dose"
                            name="dose"
                            type="numeric"
                            {...params}
                            {...register("dose")}
                        />}
                    />

                    <Typography color="error" sx={errorSx} variant="inherit">
                        {errors.dose?.message}
                    </Typography>

                    <Button onClick={handleSubmit(onSubmit)} size="large" sx={buttonSx} type="submit"
                            variant="contained">Submit</Button>
                </Box>
            </form>
        </Container>
    );
}
