import * as React from "react";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from '@mui/material/CardContent';
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";

export default function DisplayMedications() {
    const buttonSx = {
        borderRadius: 0,
        fontSize: "17px",
        height: "5.75ch",
        width: "50%"
    };

    const typographyMedicationSx = {
        textAlign: "left"
    }

    let medications;

    if (localStorage.getItem("medications") !== null) {
        let medicationList = localStorage.getItem("medications");
        let parsed = JSON.parse(medicationList);

        medications = parsed.map((medication) => {
            return (
                <Card sx={{bgcolor: "#eaeef3", m: 5}}>
                    <CardContent>
                        <Typography sx={typographyMedicationSx} variant="h5">
                            Medication: {medication.medication}
                        </Typography>
                        <Typography sx={typographyMedicationSx} variant="h5">
                            Dose: {medication.dose} mg
                        </Typography>
                    </CardContent>
                    <Box>
                        <Button size="large" sx={buttonSx} type="submit" variant="contained">Edit Med</Button>
                        <Button size="large" sx={buttonSx} type="submit" variant="contained">Delete Med</Button>
                    </Box>
                </Card>
            )
        });
    }

    return (
        <Container>
            <Box style={{flexDirection: "column"}}>
                {medications}
            </Box>
        </Container>
    )
}
