import * as React from "react";

// Material UI Components
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import Box from "@mui/material/Box";
import Breadcrumbs from "@mui/material/Breadcrumbs";
import Button from "@mui/material/Button";
import Chip from "@mui/material/Chip";
import Container from "@mui/material/Container";
import Divider from "@mui/material/Divider";
import Link from "@mui/material/Link";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import Stack from "@mui/material/Stack";
import Typography from "@mui/material/Typography";

export default function AddReminder5() {
    // Styles
    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const iconButtonSx = {
        fontSize: 75,
        ml: 2.5,
        mr: 2.5
    };

    const linkSx = {
        color: "#1976d2"
    };

    const stackSx = {
        mb: 5
    };

    const breadcrumbs = [
        <Link key="1" underline="hover" color="inherit" href="/add-reminder" sx={linkSx}>
            Name & Dose
        </Link>,
        <Link key="2" underline="hover" color="inherit" href="/add-reminder/days" sx={linkSx}>
            Days of Week
        </Link>,
        <Link key="3" underline="hover" color="inherit" href="/add-reminder/days/nums" sx={linkSx}>
            # of Times
        </Link>,
        <Link key="4" underline="hover" color="inherit" href="/add-reminder/days/nums/times" sx={linkSx}>
            # of Times
        </Link>,
        <Typography key="5" color="text.primary">
            Confirmation
        </Typography>
    ];

    function getMostRecent() {
        let reminderList = localStorage.getItem("reminders");
        let parsed = JSON.parse(reminderList);
        return parsed[parsed.length - 1];
    }

    const ChipList = (params) => {
        return (params.arr.map((index) => {
            console.log(index)
            return (<Chip key={"Chip" + index} label={"test"} variant="outlined"/>);
        }));
    };

    const ConfirmationMessage = () => {
        const medication = getMostRecent();

        console.log(medication.days);
        console.log(medication.times)

        return (
            <Box>
                <Typography variant="h5">
                    Review your reminder
                </Typography>

                <Divider/>

                <Typography variant="h5">
                    Medication: {medication.medication}
                </Typography>
                <Typography variant="h5">
                    Dose: {medication.dose} mg
                </Typography>
                <Typography variant="h5">
                    Days: <ChipList arr={medication.days}/>
                </Typography>
                <Typography variant="h5">
                    Times: <ChipList arr={medication.times}/>
                </Typography>
            </Box>
        );
    };

    const onSubmit = () => {
        let reminderList = localStorage.getItem("reminders");
        let parsed = JSON.parse(reminderList);
        let mostRecent = getMostRecent();
        mostRecent.submitted = true;
        parsed[parsed.length - 1] = mostRecent;
        localStorage.setItem("reminders", JSON.stringify(parsed));

        console.log(getMostRecent());
    };

    return (
        <Container>
            <Stack spacing={2} sx={stackSx}>
                <Breadcrumbs
                    separator={<NavigateNextIcon fontSize="small"/>}
                    aria-label="breadcrumb"
                >
                    {breadcrumbs}
                </Breadcrumbs>
            </Stack>
            <Box>
                <ConfirmationMessage/>

                <Box>
                    <Button href="/add-reminder/days/nums/times"><ArrowBackIcon sx={iconButtonSx}/></Button>

                    <Button onClick={onSubmit} size="large" sx={buttonSx} type="submit"
                            variant="contained">Submit</Button>
                </Box>
            </Box>
        </Container>
    );
}
