import * as React from "react";
import {useForm, Controller} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Material UI Components
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";

const daysOfWeekSuggestions = [
    {label: "Every day"},
    {label: "Monday"},
    {label: "Tuesday"},
    {label: "Wednesday"},
    {label: "Thursday"},
    {label: "Friday"},
    {label: "Saturday"},
    {label: "Sunday"}
];

const validationSchema = Yup.object().shape({
    daysOfWeek: Yup.array()
        .of(
            Yup.object().shape({
                value: Yup.string(),
                label: Yup.string()
            })
        )
        .min(1, "Days of the week are required")
});

export default function AddReminder2() {
    const boxSx = {
        "& .MuiTextField-root": {mb: 5, width: "100%"},
        bgcolor: "background.paper",
        ml: "50px",
        mr: "50px"
    };

    const buttonSx = {
        fontSize: "17px",
        height: "5.75ch",
        width: "100%"
    };

    const {handleSubmit, control, formState} = useForm({
        mode: "onChange",
        resolver: yupResolver(validationSchema)
    });

    const onSubmit = (data) => {
        console.log(data);
    };

    const [days] = React.useState([]);

    let reminderList = localStorage.getItem("reminders");
    let parsed = JSON.parse(reminderList);

    console.log(parsed);

    return (
        <Container>
            <form onSubmit={handleSubmit(onSubmit)} noValidate>
                <Box
                    sx={boxSx}
                >
                    <Controller
                        name="daysOfWeek"
                        control={control}
                        defaultValue={[]}
                        render={({field: {ref, ...field}, fieldState: {error}}) => (
                            <Autocomplete
                                {...field}
                                autoHighlight
                                disableClearable
                                disablePortal
                                filterSelectedOptions
                                multiple
                                getOptionDisabled={(option) => {
                                    if (days.some((day) => day.label === "Every day")) {
                                        return true;
                                    }
                                    if (days.some((day) => day.label === option.label)) {
                                        return true;
                                    }
                                }}
                                getOptionLabel={(option) => option.label}
                                id="days-autocomplete"
                                onChange={(event, value) => field.onChange(value)}
                                options={daysOfWeekSuggestions}
                                renderInput={(params) => (
                                    <TextField
                                        required
                                        error={!!error}
                                        helperText={error?.message}
                                        id="daysOfWeek"
                                        label="Days of the week"
                                        name="daysOfWeek"
                                        type="search"
                                        inputRef={ref}
                                        {...params}
                                    />
                                )}
                            />
                        )}
                    />

                    <Button disabled={!formState.isValid} size="large" sx={buttonSx} type="submit"
                            variant="contained">Submit</Button>
                </Box>
            </form>
        </Container>
    );
}
